import React, {Component} from 'react';
import {connect} from 'react-redux';
import '../CSS/Info.css';

class MedsTable extends Component {
    render() {
        const timeLabels = [];

        for(let i = 0; i<24; i++) {
            timeLabels.push(<th>{i + ":00"}</th>);
        }
        return(<div>
                <table className="medication-info">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Amount</th>
                            {timeLabels}
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.meds ? this.props.meds.map((med, i) => {
                            return(<tr key={i}>
                                    <td key={i}>{med.name}</td>
                                    <td key={i}>{med.times.filter((time) => (time)).length + "x" + med.amount}</td>
                                    {med.times.map((time, j) => {
                                        return(<td key={j}>{time ? med.amount : null}</td>);
                                    })}
                                </tr>);
                        }) : null}
                    </tbody>
                </table>
            </div>);
    }
}

function mapStateToProps(state) {
    return {meds: state.meds};
}

export default connect(mapStateToProps)(MedsTable);
