import React, {Component} from 'react';
import {connect} from 'react-redux';
import '../CSS/Info.css';

class MedsTable extends Component {
    formatDate = (num) => {
        if(num < 10) return("0" + String(num));
        return(""+num);
    }

    summarizeAmounts = (doses) => {
        let count = [];
        for(let i = 0; i<doses.length; i++) {
            let included = false;
            for(let j = 0; j<count.length; j++) {
                if(count[j].amount === doses[i].amount) {
                    count[j].total++;
                }
            }
            if(included === false) {
                count.push({amount: doses[i].amount, total: 1});
            }

        }

        var s = "";

        for(let i = 0; i<count.length; i++) {
            if(i > 0) s += " + "
            s += count[i].total + "x" + count[i].amount;
        }

        return s;
    }

    render() {
        const timeLabels = [];
        let k = 0;

        for(let i = 0; i<24; i++) {
            timeLabels.push(<th>{i + ":00"}</th>);
        }
        return(<div key={this.props.id}>
                <table className="medication-info">
                    <thead>
                        <tr>
                            <th>Nafn</th>
                            <th>Magn</th>
                            {timeLabels}
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.meds ? this.props.meds.map((med, i) => {
                            return(<tr key={i}>
                                    <td key={i}>{med.name}</td>
                                    <td>{this.summarizeAmounts(med.doses)}</td>
                                    {timeLabels.map((time, i) => {
                                        let value = "";
                                        if (this.props.meds[0].doses[k] && parseInt(this.props.meds[0].doses[k].hour) === i) {
                                            value = this.props.meds[0].doses[k].amount;
                                            k++;
                                        }
                                        return(<td>{value}</td>);
                                    })}
                                </tr>);
                        }) : null}
                    </tbody>
                </table>
            </div>);
    }
}

function mapStateToProps(state) {
    return {meds: state.meds};
}

export default connect(mapStateToProps)(MedsTable);
