import React, {Component} from 'react';
import {connect} from 'react-redux';
import '../CSS/Info.css';

class MedsTable extends Component {
    formatDate = (num) => {
        if(num < 10) return("0" + String(num));
        return(""+num);
    }

    render() {
        const timeLabels = [];
        let k = 0;

        for(let i = 0; i<24; i++) {
            timeLabels.push(<th>{i + ":00"}</th>);
        }
        return(<div key={this.props.id}>
                <table className="medication-info">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Amount</th>
                            {timeLabels}
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.meds ? this.props.meds.map((med, i) => {
                            return(<tr key={i}>
                                    <td key={i}>{med.name}</td>
                                    {timeLabels.map((time, i) => {
                                        let value = "";
                                        console.log(i);
                                        console.log(parseInt(this.props.meds[0].doses[0].hour))
                                        if (this.props.meds[0].doses[k] && parseInt(this.props.meds[0].doses[k].hour) === i) {
                                            value = this.props.meds[0].doses[k].amount;
                                            k++;
                                        }
                                        return(<td>{value}</td>);
                                    })}
                                </tr>);
                        }) : null}
                    </tbody>
                </table>
            </div>);
    }
}

function mapStateToProps(state) {
    return {meds: state.meds};
}

export default connect(mapStateToProps)(MedsTable);
