import React, {Component} from 'react';

class NewDose extends Component {
    constructor() {
        super();

        this.hour_input = React.createRef();
        this.minute_input = React.createRef();
        this.hour = "00";
        this.minute = "00";
    }

    state = {hour: "00", minute: "00"}

    formatDate = (num) => {
        if(num < 10) return("0" + String(num));
        return(""+num);
    }

    hr_up = () => {
        let hr = parseInt(this.state.hour);
        hr++;
        if(hr > 23) hr = 0;
        var state_copy = JSON.parse(JSON.stringify(this.state));
        state_copy.hour = this.formatDate(hr);
        this.setState(state_copy);
    }
    hr_down = () => {
        let hr = parseInt(this.state.hour);
        hr--;
        if(hr < 0) hr = 23;
        var state_copy = JSON.parse(JSON.stringify(this.state));
        state_copy.hour = this.formatDate(hr);
        this.setState(state_copy);
    }
    min_up = () => {
        let hr = parseInt(this.state.hour);
        let min = parseInt(this.state.minute);
        min += 30;
        if(min >= 60) {
            min = 0;
            hr++;
            if(hr > 23) hr = 0;
        }
        var state_copy = JSON.parse(JSON.stringify(this.state));
        state_copy.minute = this.formatDate(min);
        this.setState(state_copy);
    }
    min_down = () => {
        let hr = parseInt(this.state.hour);
        let min = parseInt(this.state.minute);
        min -= 30;
        if(min < 0) {
            min = 30;
            hr--;
            if(hr < 0) hr = 23;
        }
        var state_copy = JSON.parse(JSON.stringify(this.state));
        state_copy.minute = this.formatDate(min);
        state_copy.hour = this.formatDate(hr);
        this.setState(state_copy);
    }

    hourEdited = (event) => {
        console.log(event.keyCode);
        if(this.state.hour.length < 2 || event.keyCode === 8 ) {
            let state_copy = JSON.parse(JSON.stringify(this.state));
            state_copy.hour = event.target.value;
            this.setState(state_copy);
        }
    }

    minuteEdited = (event) => {
        if(this.state.minute.length < 2) {
            let state_copy = JSON.parse(JSON.stringify(this.state));
            state_copy.minute = event.target.value;
            this.setState(state_copy);
        }
    }

    render() {
        return(<div className="new-dose">
        <input type="text" className="amount" placeholder="Magn" />
        <div className="time-picker" data-time="00:00">
            <div className="hour">
                <div className="hr-up" onClick={this.hr_up} />
                <input type="number" ref={(ref) => this.hour_input = ref} className="hr" onChange={this.hourEdited.bind(this)} value={this.state.hour} />
                <div className="hr-down" onClick={this.hr_down}/>
            </div>
            <div className="separator">:</div>
            <div className="minute">
                <div className="min-up" onClick={this.min_up}/>
                <input type="number" ref={(ref) => this.minute_input = ref} className="min" onChange={this.minuteEdited.bind(this)} value={this.state.minute} />
                <div className="min-down" onClick={this.min_down} />
            </div>
        </div>
    </div>);
    }
}

export default NewDose;
