import React, {Component} from 'react';
import '../CSS/AddWindow.css';
import {addMeds} from '../Actions/add-meds-action';
import {bindActionCreators} from 'redux';
import {connect} from 'react-redux';

class AddWindow extends Component {
    state = {
        doses:[{amount: "75mg", hour:0, minute:0}]
    };

    submitMeds = () => {
        var submit_name = this.refs.name_input.value;
        var amount_name = this.refs.amount_input.value;

        if(submit_name && amount_name) {
            this.props.toggle();
            this.props.addMeds({                    
                name: submit_name,                
                amount: amount_name,
            });
        } else {
            alert("You cannot add empty medication");
        }
    }
    addDose = () => {
        this.state.doses.push({amount: "", hour:0, minute: 0})
    }

    render() {
        return(
            <div>
                <div className='dim-background' onClick={this.props.toggle}></div>
                <div className='add-window'>
                    <div className="top"><p>Bæta við lyfjagjöf</p></div>
                    <input type="text" ref="name_input" placeholder="Nafn lyfs"></input>
                    <input type="text" ref="amount_input" placeholder="Amount"></input>
                    <button className="add-dose" onClick={this.addDose}>Nýr skammtur</button>
                    {this.state.doses.map(() => {
                        return(<div className="new-dose">
                            <input type="text" className="amount" placeholder="Magn" />
                            <div className="time-picker" data-time="00:00">
                                <div className="hour">
                                    <input type="number" className="hr" />
                                </div>
                                <div className="minute">
                                    <input type="number" className="min" />
                                </div>
                            </div>
                        </div>);
                    })}
                    <button className="save" type="submit" onClick={this.submitMeds}>Vista</button>
                    <p className="error_message"></p>
                </div>
            </div>
        )
    }
}

function matchDispatchToProps(dispatch) {
    return bindActionCreators({addMeds: addMeds}, dispatch);
}


export default connect(null, matchDispatchToProps)(AddWindow);
