import React, {Component} from 'react';
import '../CSS/Info.css';
import AddWindow from './AddWindow';
import MedsTable from './MedsTable';


class Info extends Component {
    state = {
        seen: false
    };

    toggleAdd = () => {
        this.setState({
            seen: !this.state.seen
        });
    };

    render() {
        return(
            <div>
                <button className="add-meds" key={0} onClick={this.toggleAdd}>Add</button>
                {this.state.seen ? <AddWindow key={1} toggle={this.toggleAdd} /> : null}
                <MedsTable key={2}/>
            </div>
        );
    }
}

export default Info;
