import React, {Component} from 'react';
import '../CSS/AddWindow.css';
import {addMeds} from '../Actions/add-meds-action';
import {bindActionCreators} from 'redux';
import {connect} from 'react-redux';

class AddWindow extends Component {
    state = {
        doses:[{amount: "75mg", hour:0, minute:0}]
    };

    submitMeds = () => {
        var submit_name = this.refs.name_input.value;
        var amount_name = this.refs.amount_input.value;

        if(submit_name && amount_name) {
            this.props.toggle();
            this.props.addMeds({                    
                name: submit_name,                
                amount: amount_name,
            });
        } else {
            alert("You cannot add empty medication");
        }
    }
    addDose = () => {
        this.setState({doses: [...this.state.doses, {amount:"", hour:0, minute:0}]});
    }

    hr_up = (dose) => {
        dose.hour++;
        if(dose.hour > 23) dose.hour = 0;
        setTime();
    }
    hr_down = (dose) => {
        dose.hour--;
        if(dose.hour > 23) dose.hour = 0;
        setTime();
    }
    min_up = (dose) => {
        dose.minute += 30;
        if(dose.minute >= 60) {
            dose.minute = 0;
            dose.hour++;
        }
        setTime();
    }
    min_down = (dose) => {
        dose.minute -= 30;
        if(dose.minute < 0) {
            dose.hour--;
            dose.minute = 30;
        }
        setTime();
    }



    render() {
        return(
            <div>
                <div className='dim-background' onClick={this.props.toggle}></div>
                <div className='add-window'>
                    <div className="top"><p>Bæta við lyfjagjöf</p></div>
                    <input type="text" ref="name_input" placeholder="Nafn lyfs" />
                    <input type="text" ref="amount_input" placeholder="Amount" />
                    <button className="add-dose" onClick={this.addDose}>Nýr skammtur</button>
                    {this.state.doses.map((dose, i) => {
                        return(<div className="new-dose">
                            <input type="text" className="amount" placeholder="Magn" />
                            <div className="time-picker" data-time="00:00">
                                <div className="hour">
                                    <div className="hr-up" onClick={this.hr_up(dose)} />
                                    <input type="number" className="hr" value="00" />
                                    <div className="hr-down" onClick={this.hr_down(dose)}/>
                                </div>
                                <div className="separator">:</div>
                                <div className="minute">
                                    <div className="min-up" onClick={this.min_up(dose)}/>
                                    <input type="number" className="min" value="00" />
                                    <div className="min-down" onClick={this.min_down(dose)} />
                                </div>
                            </div>
                        </div>);
                    })}
                    <button className="save" type="submit" onClick={this.submitMeds}>Vista</button>
                    <p className="error_message"></p>
                </div>
            </div>
        )
    }
}

function matchDispatchToProps(dispatch) {
    return bindActionCreators({addMeds: addMeds}, dispatch);
}


export default connect(null, matchDispatchToProps)(AddWindow);
