import React, {Component} from 'react';

class NewDose extends Component {
    constructor() {
        super();

        this.hour_input = React.createRef();
        this.minute_input = React.createRef();
    }

    hr_up = (dose, i) => {
        dose.hour++;
        if(dose.hour > 23) dose.hour = 0;
        this.setTime(dose, i);
    }
    hr_down = (dose, i) => {
        dose.hour--;
        if(dose.hour > 23) dose.hour = 0;
        this.setTime(dose, i);
    }
    min_up = (dose, i) => {
        dose.minute += 30;
        if(dose.minute >= 60) {
            dose.minute = 0;
            dose.hour++;
        }
        this.setTime(dose, i);
    }
    min_down = (dose, i) => {
        dose.minute -= 30;
        if(dose.minute < 0) {
            dose.hour--;
            dose.minute = 30;
        }
        this.setTime(dose, i);
    }
    setTime = (dose, i) => {
        console.log(this.hour_input.value);
    }

    render() {
        return(<div key={i} className="new-dose">
        <input type="text" className="amount" placeholder="Magn" />
        <div className="time-picker" data-time="00:00">
            <div className="hour">
                <div className="hr-up" onClick={this.hr_up(dose, i)} />
                <input type="number" ref={(ref) => this.hour_input = ref} className="hr" defaultValue="00" />
                <div className="hr-down" onClick={this.hr_down(dose)}/>
            </div>
            <div className="separator">:</div>
            <div className="minute">
                <div className="min-up" onClick={this.min_up(dose, i)}/>
                <input type="number" ref={(ref) => this.minute_input = ref} className="min" defaultValue="00" />
                <div className="min-down" onClick={this.min_down(dose)} />
            </div>
        </div>
    </div>);
    }
}
